mapper.select:
select * from performance
  order by perfCode desc
   limit #{startRow}, #{pageSize}

SELECT lineCode AS lineCode, SUM(perfAmount) AS totalAmount, SUM(perfFair) AS totalFair, SUM(perfDefect) AS totalDefect FROM performance Where lineCode IN
#{item}
GROUP BY lineCode;

mapper.update:
update performance set 
perfFair=#{perfFair}, perfDefect=#{perfDefect}, perfDefectreason=#{perfDefectreason},perfDefectmemo=#{perfDefectmemo},
perfMemo=#{perfMemo},perfStatus=#{perfStatus}
where perfCode=#{perfCode}
