mapper.select:
SELECT i.inNum, i.buyNum, r.whseCode, c.clientCompany, i.rawCode, r.rawName, i.inCount, s.stockCount, r.rawPrice, i.inPrice, DATE_FORMAT(i.inDate, '%Y-%m-d'), i.inEmpId, i.inState FROM inMaterial i left join rawMaterial r on i.rawCode = r.rawCode left join stock s on i.rawCode = s.rawCode left join clients c on i.clientCode = c.clientCode where
i.inNum LIKE CONCAT('%', #{inNum}, '%') AND r.rawName LIKE CONCAT('%', #{rawName}, '%') AND c.clientCompany LIKE CONCAT('%', #{clientCompany}, '%') AND i.inState = #{inState}
r.rawName LIKE CONCAT('%', #{rawName}, '%') AND c.clientCompany LIKE CONCAT('%', #{clientCompany}, '%') AND i.inState = #{inState}
i.inNum LIKE CONCAT('%', #{inNum}, '%') AND c.clientCompany LIKE CONCAT('%', #{clientCompany}, '%') AND i.inState = #{inState}
i.inNum LIKE CONCAT('%', #{inNum}, '%') AND r.rawName LIKE CONCAT('%', #{rawName}, '%') AND i.inState = #{inState}
i.inNum LIKE CONCAT('%', #{inNum}, '%') AND r.rawName LIKE CONCAT('%', #{rawName}, '%') AND c.clientCompany LIKE CONCAT('%', #{clientCompany}, '%')
c.clientCompany LIKE CONCAT('%', #{clientCompany}, '%') AND i.inState = #{inState}
r.rawName LIKE CONCAT('%', #{rawName}, '%') AND i.inState = #{inState}
r.rawName LIKE CONCAT('%', #{rawName}, '%') AND c.clientCompany LIKE CONCAT('%', #{clientCompany}, '%')
i.inNum LIKE CONCAT('%', #{inNum}, '%') AND i.inState = #{inState}
i.inNum LIKE CONCAT('%', #{inNum}, '%') AND c.clientCompany LIKE CONCAT('%', #{clientCompany}, '%')
i.inNum LIKE CONCAT('%', #{inNum}, '%') AND r.rawName LIKE CONCAT('%', #{rawName}, '%')
i.inState = #{inState}
c.clientCompany LIKE CONCAT('%', #{clientCompany}, '%')
r.rawName LIKE CONCAT('%', #{rawName}, '%')
i.inNum LIKE CONCAT('%', #{inNum}, '%')
1=1
ORDER BY i.inNum DESC LIMIT #{startRow}, #{pageSize}

SELECT count(*) FROM ( SELECT i.inNum, i.buyNum, r.whseCode, c.clientCompany, i.rawCode, r.rawName, i.inCount, s.stockCount, r.rawPrice, i.inPrice, DATE_FORMAT(i.inDate, '%Y-%m-d'), i.inEmpId, i.inState FROM inMaterial i left join rawMaterial r on i.rawCode = r.rawCode left join stock s on i.rawCode = s.rawCode left join clients c on i.clientCode = c.clientCode where
i.inNum LIKE CONCAT('%', #{inNum}, '%') AND r.rawName LIKE CONCAT('%', #{rawName}, '%') AND c.clientCompany LIKE CONCAT('%', #{clientCompany}, '%') AND i.inState = #{inState}
r.rawName LIKE CONCAT('%', #{rawName}, '%') AND c.clientCompany LIKE CONCAT('%', #{clientCompany}, '%') AND i.inState = #{inState}
i.inNum LIKE CONCAT('%', #{inNum}, '%') AND c.clientCompany LIKE CONCAT('%', #{clientCompany}, '%') AND i.inState = #{inState}
i.inNum LIKE CONCAT('%', #{inNum}, '%') AND r.rawName LIKE CONCAT('%', #{rawName}, '%') AND i.inState = #{inState}
i.inNum LIKE CONCAT('%', #{inNum}, '%') AND r.rawName LIKE CONCAT('%', #{rawName}, '%') AND c.clientCompany LIKE CONCAT('%', #{clientCompany}, '%')
c.clientCompany LIKE CONCAT('%', #{clientCompany}, '%') AND i.inState = #{inState}
r.rawName LIKE CONCAT('%', #{rawName}, '%') AND i.inState = #{inState}
r.rawName LIKE CONCAT('%', #{rawName}, '%') AND c.clientCompany LIKE CONCAT('%', #{clientCompany}, '%')
i.inNum LIKE CONCAT('%', #{inNum}, '%') AND i.inState = #{inState}
i.inNum LIKE CONCAT('%', #{inNum}, '%') AND c.clientCompany LIKE CONCAT('%', #{clientCompany}, '%')
i.inNum LIKE CONCAT('%', #{inNum}, '%') AND r.rawName LIKE CONCAT('%', #{rawName}, '%')
i.inState = #{inState}
c.clientCompany LIKE CONCAT('%', #{clientCompany}, '%')
r.rawName LIKE CONCAT('%', #{rawName}, '%')
i.inNum LIKE CONCAT('%', #{inNum}, '%')
1=1
) AS subquery

mapper.update:
update stock
SET stockCount = stockCount + #{inCount}
WHERE rawCode = #{rawCode}
