mapper.select:
SELECT COUNT(*) FROM ( SELECT o.outCode outCode, o.prodCode prodCode, DATE_FORMAT(o.outDate, '%Y-%m-d') outDate, o.outEmpId outEmpId, o.outMemo outMemo, DATE_FORMAT(o.outRedate , '%Y-%m-%d') outRedate, o.outCount outCount, o.outPrice outPrice, o.sellCode sellCode, s.sellCount sellCount, DATE_FORMAT(s.sellDuedate, '%Y-%m-%d') sellDuedate, s.sellState sellState, s.clientCode clientCode, c.clientCompany clientCompany, p.prodName prodName, p.prodPrice prodPrice, stockTestTest.whseCount whseCount FROM outProduct o LEFT JOIN sellTestTest s ON o.sellCode = s.sellCode LEFT JOIN clients c ON s.clientCode = c.clientCode LEFT JOIN product p ON o.prodCode = p.prodCode LEFT JOIN stockTestTest ON o.prodCode = stockTestTest.prodCode
AND o.outCode like CONCAT('%', #{outCode}, '%')
AND p.prodName like CONCAT('%', #{prodName}, '%')
AND c.clientCompany like CONCAT('%', #{clientCompany}, '%')
) AS subquery;

mapper.update:
UPDATE sellTestTest
SET sellState = #{sellState}
WHERE
sellCode = #{sellCode}

UPDATE outProduct
SET outDate = #{outDate}
WHERE
outCode = #{outCode}

UPDATE outProduct
SET outRedate = #{outRedate}
WHERE
outCode = #{outCode}

UPDATE stockTestTest
SET whseCount = whseCount -
#{outCount}
WHERE prodCode = #{prodCode}

UPDATE outProduct
SET outCount = #{outCount}, outMemo
= #{outMemo}
WHERE outCode = #{outCode}
