mapper.select:
SELECT o.outCode outCode, o.prodCode prodCode, o.outDate outDate, o.outEmpId outEmpId, o.outMemo outMemo, o.outRedate outRedate, o.outCount outCount, o.outPrice outPrice, o.sellCode sellCode, s.sellCount sellCount, s.sellDuedate sellDuedate, s.sellState sellState, s.clientCode clientCode, c.clientCompany clientCompany, p.prodName prodName, p.prodPrice prodPrice, stockTestTest.whseCount whseCount FROM outProduct o JOIN sellTestTest s ON o.sellCode = s.sellCode JOIN clients c ON s.clientCode = c.clientCode JOIN product p ON o.prodCode = p.prodCode JOIN stockTestTest ON o.prodCode = stockTestTest.prodCode WHERE
o.outCode LIKE CONCAT('%', #{outCode}, '%') AND p.prodName LIKE CONCAT('%', #{prodName}, '%') AND c.clientCompany LIKE CONCAT('%', #{clientCompany}, '%') AND s.sellState = #{sellState}
o.outCode LIKE CONCAT('%', #{outCode}, '%') AND p.prodName LIKE CONCAT('%', #{prodName}, '%') AND c.clientCompany LIKE CONCAT('%', #{clientCompany}, '%')
o.outCode LIKE CONCAT('%', #{outCode}, '%') AND p.prodName LIKE CONCAT('%', #{prodName}, '%') AND s.sellState = #{sellState}
p.prodName LIKE CONCAT('%', #{prodName}, '%') AND c.clientCompany LIKE CONCAT('%', #{clientCompany}, '%') AND s.sellState = #{sellState}
o.outCode LIKE CONCAT('%', #{outCode}, '%') AND p.prodName LIKE CONCAT('%', #{prodName}, '%')
o.outCode LIKE CONCAT('%', #{outCode}, '%') AND c.clientCompany LIKE CONCAT('%', #{clientCompany}, '%')
o.outCode LIKE CONCAT('%', #{outCode}, '%') AND s.sellState = #{sellState}
p.prodName LIKE CONCAT('%', #{prodName}, '%') AND c.clientCompany LIKE CONCAT('%', #{clientCompany}, '%')
p.prodName LIKE CONCAT('%', #{prodName}, '%') AND s.sellState = #{sellState}
c.clientCompany LIKE CONCAT('%', #{clientCompany}, '%') AND s.sellState = #{sellState}
o.outCode LIKE CONCAT('%', #{outCode}, '%')
p.prodName LIKE CONCAT('%', #{prodName}, '%')
c.clientCompany LIKE CONCAT('%', #{clientCompany}, '%')
s.sellState = #{sellState}
1=1
ORDER BY o.prodCode DESC LIMIT #{startRow}, #{pageSize}

SELECT COUNT(*) FROM ( SELECT o.outCode outCode, o.prodCode prodCode, DATE_FORMAT(o.outDate, '%Y-%m-d') outDate, o.outEmpId outEmpId, o.outMemo outMemo, DATE_FORMAT(o.outRedate , '%Y-%m-%d') outRedate, o.outCount outCount, o.outPrice outPrice, o.sellCode sellCode, s.sellCount sellCount, DATE_FORMAT(s.sellDuedate, '%Y-%m-%d') sellDuedate, s.sellState sellState, s.clientCode clientCode, c.clientCompany clientCompany, p.prodName prodName, p.prodPrice prodPrice, stockTestTest.whseCount whseCount FROM outProduct o JOIN sellTestTest s ON o.sellCode = s.sellCode JOIN clients c ON s.clientCode = c.clientCode JOIN product p ON o.prodCode = p.prodCode JOIN stockTestTest ON o.prodCode = stockTestTest.prodCode WHERE
o.outCode LIKE CONCAT('%', #{outCode}, '%') AND p.prodName LIKE CONCAT('%', #{prodName}, '%') AND c.clientCompany LIKE CONCAT('%', #{clientCompany}, '%') AND s.sellState = #{sellState}
o.outCode LIKE CONCAT('%', #{outCode}, '%') AND p.prodName LIKE CONCAT('%', #{prodName}, '%') AND c.clientCompany LIKE CONCAT('%', #{clientCompany}, '%')
o.outCode LIKE CONCAT('%', #{outCode}, '%') AND p.prodName LIKE CONCAT('%', #{prodName}, '%') AND s.sellState = #{sellState}
p.prodName LIKE CONCAT('%', #{prodName}, '%') AND c.clientCompany LIKE CONCAT('%', #{clientCompany}, '%') AND s.sellState = #{sellState}
o.outCode LIKE CONCAT('%', #{outCode}, '%') AND p.prodName LIKE CONCAT('%', #{prodName}, '%')
o.outCode LIKE CONCAT('%', #{outCode}, '%') AND c.clientCompany LIKE CONCAT('%', #{clientCompany}, '%')
o.outCode LIKE CONCAT('%', #{outCode}, '%') AND s.sellState = #{sellState}
p.prodName LIKE CONCAT('%', #{prodName}, '%') AND c.clientCompany LIKE CONCAT('%', #{clientCompany}, '%')
p.prodName LIKE CONCAT('%', #{prodName}, '%') AND s.sellState = #{sellState}
c.clientCompany LIKE CONCAT('%', #{clientCompany}, '%') AND s.sellState = #{sellState}
o.outCode LIKE CONCAT('%', #{outCode}, '%')
p.prodName LIKE CONCAT('%', #{prodName}, '%')
c.clientCompany LIKE CONCAT('%', #{clientCompany}, '%')
s.sellState = #{sellState}
1=1
) AS subquery;

mapper.update:
UPDATE sellTestTest
SET sellState = #{sellState}
WHERE sellCode = #{sellCode}

UPDATE outProduct
SET outDate = #{outDate}
WHERE outCode = #{outCode}

UPDATE outProduct
SET outRedate = #{outRedate}
WHERE outCode = #{outCode}

UPDATE stockTestTest
SET whseCount = #{whseCount} - #{outCount}
WHERE prodCode = #{prodCode}

UPDATE outProduct
SET outCount = #{outCount}, outMemo = #{outMemo}
WHERE outCode = #{outCode}
